exports = module.exports = () => {
    browser.timeouts("script", 30000);

    browser.addCommand("waitForPage", (timeout) => {
        return browser
            .waitUntil(() => {
                return browser.getText("#root")
                    .then((text) => {
                        return text && text.length > 0;
                    });
            }, timeout ? timeout : 5000);
    });

    browser.addCommand("loadAndWaitForPage", (url, timeout) => {
        return browser
            .url(url)
            .waitForPage(timeout);
    });
};

