/**
 * Main entry point for app.
 */
import Vue from "vue";
import {App} from "./app";
import router from "./router";

/**
 * Function to bootstrap the application
 * @returns {void}
 */
export function bootstrap () {
    Vue.config.productionTip = false;

    return new Vue({
        "el": "#root",
        router,
        "template": "<App/>",
        "components": {App}
    });
}

