/**
 * Gulp tasks for linting app with stylint.
 */
const gulp = require("gulp");
const stylint = require("gulp-stylint");
const path = require("path");
const streamToPromise = require("stream-to-promise");
const display = require("../../util/display");
const uc = require("../../util/unite-config");
gulp.task("build-css-lint-app", async () => {
    display.info("Running", "Stylint for App");
    const uniteConfig = await uc.getUniteConfig();
    return streamToPromise(gulp.src(path.join(uniteConfig.dirs.www.cssSrc, `**/*.${uniteConfig.styleExtension}`))
        .pipe(stylint({
            reporter: "stylint-stylish"
        }))
        .pipe(stylint.reporter())
        .pipe(stylint.reporter("fail"))
        .on("error", (err) => {
            display.error("Stylint failed", err);
            process.exit(1);
        }));
});
// Generated by UniteJS
