/**
 * Gulp tasks for linting modules.
 */
const gulp = require("gulp");
const eslint = require("gulp-eslint");
const path = require("path");
const streamToPromise = require("stream-to-promise");
const display = require("../../util/display");
const uc = require("../../util/unite-config");
gulp.task("build-lint", async () => {
    display.info("Running", "ESLint");
    const uniteConfig = await uc.getUniteConfig();
    return streamToPromise(gulp.src(path.join(uniteConfig.dirs.www.src, `**/*.${uc.extensionMap(uniteConfig.sourceExtensions)}`))
        .pipe(eslint())
        .pipe(eslint.format())
        .pipe(eslint.results(result => {
            if (result.errorCount > 0) {
                process.exit(1);
            }
        })));
});
// Generated by UniteJS
