/**
 * Gulp tasks for linting modules.
 */
const gulp = require("gulp");
const eslint = require("gulp-eslint");
const minimist = require("minimist");
const path = require("path");
const streamToPromise = require("stream-to-promise");
const display = require("../../util/display");
const uc = require("../../util/unite-config");
gulp.task("unit-lint", async () => {
    display.info("Running", "ESLint");
    const knownOptions = {
        default: {
            grep: "*"
        },
        string: [
            "grep"
        ]
    };
    const options = minimist(process.argv.slice(2), knownOptions);
    const uniteConfig = await uc.getUniteConfig();
    return streamToPromise(gulp.src(path.join(uniteConfig.dirs.www.unitTestSrc, `**/${options.grep}.${uc.extensionMap(uniteConfig.sourceExtensions)}`))
        .pipe(eslint())
        .pipe(eslint.format())
        .pipe(eslint.results(result => {
            if (result.errorCount > 0) {
                process.exit(1);
            }
        })));
});
// Generated by UniteJS
