/**
 * Gulp tasks for building css.
 */
const display = require("./util/display");
const gulp = require("gulp");
const path = require("path");
const uc = require("./util/unite-config");
const asyncUtil = require("./util/async-util");

gulp.task("build-css-components", async () => {
    display.info("Running", "CSS for Components");

    const uniteConfig = await uc.getUniteConfig();

    return asyncUtil.stream(gulp.src(path.join(uniteConfig.directories.src, "**/*.css"))
        .pipe(gulp.dest(uniteConfig.directories.dist)));
});

