/**
 * Gulp tasks for serving site.
 */
const gulp = require("gulp");
const browserSync = require("browser-sync");

gulp.task("serve", (cb) => {
    const bs = browserSync.create();
    bs.init({
        "https": false,
        "online": true,
        "open": true,
        "port": 9000,
        "server": {"baseDir": ["."]}
    }, cb);
});

gulp.task("serve-secure", (cb) => {
    const bs = browserSync.create();
    bs.init({
        "https": true,
        "online": true,
        "open": true,
        "port": 9000,
        "server": {"baseDir": ["."]}
    }, cb);
});

